import React, { useEffect, useRef, useState } from 'react'
import Client from './Client'
import Editor from './Editor'
import {
  useNavigate,
  useLocation,
  Navigate,
  useParams,
} from "react-router-dom";

function EditorPage() {
  const socketRef = useRef(null)
  const Location = useLocation()
  const { roomId } = useParams()

  useEffect(() => {
    const init = async () => {
      socketRef.current = await initSocket()
      socketRef.current.emit(ACTIONS.JOIN, {
        roomId,
        username: Location.state?.username,
      })
    }
    init();
  }, [])





  const [clients, setClient] = useState([
    {socketId:1, userName:"Ambika"},
    {socketId:2, userName:"Krishna"},
  ])
  return (
    <div className='container-fluid vh-100'>
      <div className='row h-100'>
        <div className='col-md-2 text-light d-flex flex-column h-100' 
        style={{backgroundColor: '#700000', borderRight : '15px solid white'}}>
          <img 
            className='img-fluid mx-auto' 
            src='/images/Home Page Logo.png' 
            alt='CollabChat' 
            style={{filter: 'invert(1)', marginTop: '12px'}}>
          </img>
          <hr style={{marginTop: '-5px'}}/>
          {/* client list container */}
          <div className='d-flex flex-column overflow-auto'>
            {clients.map((client) => (
              <Client key={client.socketId} username={client.userName}/>
            ))}
          </div>
          {/* buttons */}
          <div className='mt-auto'>
            <hr/>
            <button className='btn btn-success'>Copy Room ID</button>
            <button className='btn btn-danger mt-2 mb-3 px-3 btn-block'>
              Leave Room
            </button>
          </div>
        </div>
        {/* Editor */}
        <div className='col-md-10 d-flex flex-column h-100' style={{backgroundColor: '#FFB3B3'}}>
          <Editor />
        </div>
      </div>
    </div>
  )
}

export default EditorPage
